let app = new Vue({
  		el: '#app' ,
  name: 'WebflowSearchNavbar',
  data() {
    return {
      showDropdown: false,
      showSeparator: true,
      dropdownLabel: 'Talent',
      searchSelected: false,
      mobileTabsSelectedIndex: 0,
      searchOptions: [
        { 
          label: 'Talent',
          url: '/search/profiles',
          description: 'Hire professionals and agencies',
          icon: '',
        },
        {
          label: 'Projects',
          url: '/services/search',
          description: 'Buy ready-to-start services',
          icon: '',
        },
        {
          label: 'Jobs',
          url: '/search/jobs',
          url: '/nx/jobs/search/',
          description: 'Apply to jobs posted by clients',
          icon: '',
        },
      ],
      submitUrl: '/search/profiles',
    }
  },
  mounted() {
  },
  methods: {
    onDropdownClick() {
      this.showDropdown = !this.showDropdown;
    },
    onDropdownBlur() {
      this.showDropdown = false;
    },
    onDropdownItemClick(index) {
      this.dropdownLabel = this.searchOptions[index].label;
      this.showDropdown = false;
      this.submitUrl = this.searchOptions[index].url;

      this.$refs.search.focus();
    },
    toggleSeparator(params) {
      if (typeof params.searchSelected !== 'undefined') {
        this.searchSelected = params.searchSelected
      }

      if (!this.searchSelected) {
        this.showSeparator = !this.showSeparator;
      }
    },
    toggleSlider() {
      this.$refs.slider.classList.toggle('wf-search-nav-slide-in');
    },
    onTabClick(index) {
      this.mobileTabsSelectedIndex = index;
      this.submitUrl = this.searchOptions[index].url;
    },
  },
});

if (localStorage) { // let's not crash if some user has IE7
        var index = parseInt(localStorage['tab'] || '0');
        activate($('.blog-tab-link.w-tab-link').eq(index));
    }
    // When a link is clicked
    $(".blog-tab-link.w-tab-link").click(function() {
        if (localStorage) localStorage['tab'] = $(this).index();
        activate($(this));
    });


    var Webflow = Webflow || [];
    Webflow.push(function() {
        var tabName = getParam('tab');
        if (tabName) $('.' + tabName).triggerHandler('click');
    });

    function getParam(name) {
        name = name.replace(/[\[]/, "\\[").replace(/[\]]/, "\\]");
        var regex = new RegExp("[\\?&]" + name + "=([^&#]*)"),
            results = regex.exec(location.search);
        return results == null ? "" : decodeURIComponent(results[1].replace(/\+/g, " "));
    }

    function activate(tab) {
        // switch all tabs off
        $(".blog-tab-link.w--current").removeClass("w--current");

        // switch this tab on
        tab.addClass("w--current");
    }

